import React, { Fragment } from "react";

const Artist = (props) => {

    const genresToString = (genres, length) => {
        let return_string = "";
        let count = 0;
        genres.forEach((element) => {
          count+1 === length
            ? (return_string += element)
            : (return_string += element + ", ");
            count++; 
        });
        return return_string;
      };

    const genres = genresToString(props.genres, props.genres.length)

  return (
    <div className="pt-20">
        <a href={props.link}><img className="float-left" src={props.images[1].url} height="200" width="100" alt=""></img></a>
        <div>
            <span>{props.name}</span>
            <br></br>
            <span>Followers: {props.followers}</span>
            <br></br>
            <span>{genres}</span>
        </div>
    </div>
  );
};

export default Artist
