import React, { Fragment, useEffect, useState } from "react";
import TrackItem from "./TrackItem";

const TopTracks = (props) => {
  console.log(props.topTracks);

  console.log(props.topTracks.items[0].external_ids.isrc);
  console.log(props.topTracks.items[1].external_ids.isrc);

  const [timeFrame, setTimeFrame] = useState("long_term");
  const [allTime, setAllTime] = useState("true");
  const [sixMonths, setSixMonths] = useState("false");
  const [lastMonth, setLastMonth] = useState("false");

  const onClickHandler = (string) => {
    if (string === "long_term") {
      setTimeFrame(string)
    } else if (string === "medium_term") {
      setTimeFrame(string)
    } else if (string === "short_term") {
      setTimeFrame(string)
    }
  };

  return (
    <Fragment>
      <h1>The Top Tracks Page</h1>
      <div className="space-x-4">
        <button onClick={onClickHandler("long_term")} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          All Time
        </button>
        <button onClick={onClickHandler("medium_term")} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Past 6 Months
        </button>
        <button onClick={onClickHandler("short_term")} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Last Month
        </button>
      </div>
      <div>
        {/* Render a list of objects and pass title and artists to TrackItem component */}
        {props.topTracks.items.map((track) => (
          <ul key={track.external_ids.isrc}>
            <TrackItem
              title={track.name}
              artists={track.artists}
              picture={track.album.images}
            />
          </ul>
        ))}
      </div>
    </Fragment>
  );
};

export default TopTracks;
