import React from "react";

const clientId = '12dac98b658f492091542cd6c4babb6c'
const redirectUri = 'http://localhost:3000'
const scope = 'user-read-recently-played user-read-email user-top-read playlist-modify-public user-library-modify user-library-read playlist-modify-private'

let AUTH_URL = 'https://accounts.spotify.com/authorize';
AUTH_URL += '?response_type=token';
AUTH_URL += '&client_id=' + clientId;
AUTH_URL += '&scope=' + scope;
AUTH_URL += '&redirect_uri=' + redirectUri;



const Login = () => {
    return <a href={AUTH_URL}>Login With Spotify</a>
};

export default Login;
