import React from "react";

const ArtistPage = (props) => {
  return (
    <div>
      <div className="grid grid-cols-1 place-items-center m-5">
        <button
          className="bg-cyan-400 rounded-lg py-2 px-4 text-white font-bold hover:scale-105 hover:bg-cyan-500 transition ease-in-out duration-300"
          onClick={props.searchButtonClick}
        >
          Search A Different Artist
        </button>
      </div>
      <div className="grid grid-cols-1 place-items-center">
        <div className="flex place-items-center m-2 border-2 border-cyan-400 bg-cyan-400 rounded-lg">
          <img
            className="rounded-lg m-4"
            src={props.artistInfo.images[0].url}
            alt=""
            height={100}
            width={100}
          />
          <span className="m-4 text-5xl font-serif text-stone-300">{props.artistInfo.name}</span>
        </div>
      </div>
      <div className="">{`${props.artistAlbums}`}</div>
      <div className="">{`${props.artistTopTracks}`}</div>
      <div className="">{`${props.artistRelatedArtists}`}</div>
    </div>
  );
};

export default ArtistPage;
