import React, { Fragment, useEffect, useState } from "react";
import useAuth from "../../../useAuth";
import SpotifyWebApi from "spotify-web-api-node";
import SearchBar from "../../UI/SearchBar";

const spotifyApi = new SpotifyWebApi({
  clientId: process.env.clientId
})

const Explore = (props) => {
  const access_token = useAuth(props.code);

  const [searchInput, setSearchInput] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState();

  const choose_artist = (artist) => {
    setSelectedArtist(artist);
    setSearchInput("");
  }

  useEffect(() => {
    if (!access_token) return 
    spotifyApi.setAccessToken(access_token);
  }, [access_token])

  useEffect(() => {
    if (!access_token) return 
    if (!searchInput) setSearchResults([])

    spotifyApi.searchArtists(searchInput).then((res) => {
      console.log(res.body)
    })
  }, [access_token, searchInput])


  console.log(props.newReleases);
  console.log(props.recommendations);


  return (
    <div>
      <SearchBar />
    </div>
  );
};

export default Explore;
