import React, { Fragment, useEffect, useState } from "react";
import Selector from "../../UI/Selector";
import TrackItem from "./TrackItem";

const TopTracks = (props) => {
  console.log(props.topTracksLongTerm);
  // console.log(props.topTracks.items[0].external_ids.isrc);
  // console.log(props.topTracks.items[1].external_ids.isrc);

  console.log("Top Tracks API CALLED");

  // Dynamic String to render proper data based off which button was clicked. Use State?

  const [timeFrame, setTimeFrame] = useState("LongTerm");

  const topTracksAllTime = () => {
    setTimeFrame("topTracksLongTerm");
  };

  const topTracksSixMonths = () => {
    setTimeFrame("topTracksMediumTerm");
  };

  const topTracksLastMonth = () => {
    setTimeFrame("topTracksShortTerm");
  };

  return (
    <Fragment>
      <h1>The Top Tracks Page</h1>
      <Selector
        allTime={topTracksAllTime} sixMonths={topTracksSixMonths} lastMonth={topTracksLastMonth}
      />
      <div>
        {/* Render a list of objects and pass title and artists to TrackItem component */}
        {props.timeFrame.items.map((track) => (
          <ul key={track.external_ids.isrc}>
            <TrackItem
              title={track.name}
              artists={track.artists}
              picture={track.album.images}
            />
          </ul>
        ))}
      </div>
    </Fragment>
  );
};

export default TopTracks;
