import React from "react";

const Artist = (props) => {
  const genresToString = (genres, length) => {
    let return_string = "";
    let count = 0;
    genres.forEach((element) => {
      count + 1 === length
        ? (return_string += element)
        : (return_string += element + ", ");
      count++;
    });
    return return_string;
  };

  return (
    <div className="pl-40 pr-40 pt-4">
      <div className="flex items-center border border-indigo-600 rounded-lg hover:shadow-lg transition duration-300 ease-in-out">
        <img
          className="rounded-lg"
          src={props.images[1].url}
          height="200"
          width="100"
          alt=""
        ></img>
        <div className="pl-3">
          <a>
            <span className="font-bold text-xl hover:-translate-y-1 hover:scale-110 hover:bg-indigo-500 duration-300 text-white font-bold py-2 px-4 rounded transition ease-in-out delay 150">
              {props.name}
            </span>
          </a>
          <br></br>
          <span>Followers: {props.followers.toLocaleString("en-US")}</span>
        </div>
      </div>
    </div>
  );
};

export default Artist;
