import React, { useState } from "react";
import ArtistSearchResult from "./ArtistSearchResult";

const Explore = (props) => {

  const [displayingArtist, setDisplayingArtist] = useState();

  const chooseArtist = (artist) => {
    setDisplayingArtist(artist)
  }

  console.log(displayingArtist);

  return (
    <div>
      <div className="grid grid-cols-1 place-items-center">
        <input
          className="rounded-lg border-2 border-cyan-400"
          style={{ width: "75%" , height: "50px", fontSize: "30px"}}
          placeholder="Search an Artist to Explore!"
          onChange={(e) => {
            props.getInput(e.target.value);
          }}
        ></input>
      </div>
      <div>
        {props.searchResults.map((artist) => (
          <ul key={artist.id}>
            <ArtistSearchResult
              artistName={artist.name}
              link={artist.images[0].url}
              artistId={artist.id}
              onClickHandler={chooseArtist}
            />
          </ul>
        ))}
      </div>
    </div>
  );
};

export default Explore;
