import React, { Fragment, useState } from "react";
import Artists from "./Artists";

const TrackItem = (props) => {

  const [isHovering, setIsHovering] = useState(false);

  const handleMouseOver = () => {
    setIsHovering(true);
  };

  const handleMouseOut = () => {
    setIsHovering(false);
  }

  const rank_one_hover = "text-cyan-400 transition duration-700";
  const rank_one_idle = "text-yellow-400 ease-in-out duration-700";

  return (
    <div className="pt-4 pl-20 pr-20">
      <div onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} className="flex pt-1 pb-1 pl-3 items-center border-2 border-sky-500 rounded-lg hover:shadow-xl transition duration-500">
        <h1 className={`${isHovering === true ? "text-cyan-400 transition duration-700":"ease-in-out duration-700"} font-bold`}>{props.rank}</h1>
        <div className="pl-3 flex items-center">
          <a href={props.link} target="_blank" rel="noreferrer">
            <img className="rounded-lg" src={props.picture[2].url} alt="" />
          </a>
          <div className="pl-2">
            <a
              href={props.link}
              target="_blank"
              rel="noreferrer"
              className={isHovering === true ? rank_one_hover : rank_one_idle}
            >
              <span className="font-bold">{props.title}</span>
            </a>
            <Artists artists={props.artists} onHover={`${isHovering === true ? "text-cyan-400 transition duration-700":"text-black ease-in-out duration-700"}`}/>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TrackItem;
