import React from "react";

const Artist = (props) => {
  const genresToString = (genres, length) => {
    let return_string = "";
    let count = 0;
    genres.forEach((element) => {
      count + 1 === length
        ? (return_string += element)
        : (return_string += element + ", ");
      count++;
    });
    return return_string;
  };

  const genres_input = props.genres.slice(0, 4);
  const genres = genresToString(genres_input, genres_input.length);

  return (
    <div className="pl-20 pr-20 hover:shadow-lg transition duration-300 ease-in-out">
      <div className="flex items-center border border-indigo-600 rounded-lg">
        <img
          className="rounded-lg"
          src={props.images[1].url}
          ß
          height="200"
          width="100"
          alt=""
        ></img>
        <div className="">
          <span className="font-bold text-xl">{props.name}</span>
          <br></br>
          <span>Followers: {props.followers.toLocaleString("en-US")}</span>
        </div>
      </div>
    </div>
  );
};

export default Artist;
