import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar/Navbar";
import { Routes, Route, Navigate } from "react-router-dom";
import Explore from "./components/pages/Explore/Explore";
import Recent from "./components/pages/Recent/Recent";
import TopTracks from "./components/pages/TopTracks/TopTracks";
import TopArtists from "./components/pages/TopArtists/TopArtists";
import useAuth from "./useAuth";
import SpotifyWebApi from "spotify-web-api-node";

const spotifyApi = new SpotifyWebApi({
  clientId: "12dac98b658f492091542cd6c4babb6c",
});

const Home = ({ code }) => {
  const accessToken = useAuth(code);

  const [topArtists, setTopArtists] = useState();
  const [topTracks, setTopTracks] = useState();
  const [recentlyPlayed, setRecentlyPlayed] = useState();
  const [newReleases, setNewReleases] = useState();

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.setAccessToken(accessToken);
  }, [accessToken]);

  useEffect(() => {
    if (!accessToken) return;
    spotifyApi
      .getMyTopArtists({ time_range: "long_term", limit: 50 })
      .then((res) => {
        console.log(res.body);
        setTopArtists(res.body);
      })
      .catch((err) => {
        console.log(err);
      });

    spotifyApi
      .getMyTopTracks({ time_range: "long_term", limit: 50 })
      .then((res) => {
        setTopTracks(res.body);
      })
      .catch((err) => {
        console.log(err);
      });

    spotifyApi
      .getMyRecentlyPlayedTracks()
      .then((res) => {
        setRecentlyPlayed(res.body);
      })
      .catch((err) => {
        console.log(err);
      });

    spotifyApi
      .getNewReleases()
      .then((res) => {
        setNewReleases(res.body);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [accessToken]);

  // Render the different pages conditionally
  return (
    <div>
      <Navbar />
      <Routes>
        <Route path="/" element={<Navigate to="/explore" />} />
        <Route path="/explore" element={<Explore />} />
        <Route
          path="/recent"
          element={<Recent recentlyPlayed={recentlyPlayed} />}
        />
        <Route
          path="top-artists"
          element={<TopArtists topArtists={topArtists} />}
        />
        <Route
          path="/top-tracks"
          element={<TopTracks topTracks={topTracks} />}
        />
      </Routes>
    </div>
  );
};

export default Home;
